using System.Reflection;using Application.Common.Behaviors;using FluentValidation;using MediatR;using Microsoft.Extensions.DependencyInjection;namespace Application;public static class DependencyInjection{    public static IServiceCollection AddApplication(this IServiceCollection service)    {        service.AddMediatR(Assembly.GetExecutingAssembly());        service.AddValidatorsFromAssemblies(new[] { Assembly.GetExecutingAssembly() });        service.AddTransient(typeof(IPipelineBehavior<,>),             typeof(ValidationBehavior<,>));        service.AddTransient(typeof(IPipelineBehavior<,>),            typeof(LoggingBehavior<,>));        return service;    }}