using Application.Interfaces;using Domain;using MediatR;namespace Application.ShortUrls.Commands.CreateShortUrl;public class CreateShortUrlCommandHandler : IRequestHandler<CreateShortUrlCommand>{    private readonly IShortUrlDbContext _dbContext;    public CreateShortUrlCommandHandler(IShortUrlDbContext dbContext) =>        _dbContext = dbContext;    public async Task<Unit> Handle(CreateShortUrlCommand request, CancellationToken cancellationToken)    {        var shortUrl = new ShortUrl        {            Destination = request.Destination,            Path = request.Path        };        await _dbContext.ShortUrls.AddAsync(shortUrl, cancellationToken);        await _dbContext.SaveChangesAsync(cancellationToken);        return Unit.Value;    }}