using FluentValidation;namespace Application.ShortUrls.Commands.CreateShortUrl;public class CreateShortUrlCommandValidator : AbstractValidator<CreateShortUrlCommand>{    public CreateShortUrlCommandValidator()    {        RuleFor(createShortUrlCommand => createShortUrlCommand.Destination).NotEmpty();        RuleFor(createShortUrlCommand => createShortUrlCommand.Path).NotEmpty();        RuleFor(createShortUrlCommand => createShortUrlCommand.Path).Matches(@"^[a-zA-Z0-9_-]*$");    }}