using FluentValidation;namespace Application.ShortUrls.Commands.DeleteShortUrl;public class DeleteShortUrlCommandValidator : AbstractValidator<DeleteShortUrlCommand>{    public DeleteShortUrlCommandValidator()    {        RuleFor(deleteShortUrlCommand => deleteShortUrlCommand.Path).NotEmpty();        RuleFor(deleteShortUrlCommand => deleteShortUrlCommand.Path).Matches(@"^[a-zA-Z0-9_-]*$");    }}