using FluentValidation;namespace Application.ShortUrls.Commands.UpdateShortUrl;public class UpdateShortUrlCommandValidator : AbstractValidator<UpdateShortUrlCommand>{    public UpdateShortUrlCommandValidator()    {        RuleFor(updateShortUrlCommand => updateShortUrlCommand.Destination).NotEmpty();        RuleFor(updateShortUrlCommand => updateShortUrlCommand.Path).NotEmpty();        RuleFor(updateShortUrlCommand => updateShortUrlCommand.Path).Matches(@"^[a-zA-Z0-9_-]*$");    }}