using Application.Common.Mappings;using AutoMapper;using Domain;namespace Application.ShortUrls.Queries.GetShortUrl;public class ShortUrlDto : IMapWith<ShortUrl>{    public string Destination { get; set; }    public void Mapping(Profile profile)    {        profile.CreateMap<ShortUrl, ShortUrlDto>()            .ForMember(shortDto => shortDto.Destination,                opt                    => opt.MapFrom(shortUrl => shortUrl.Destination));    }}