using Application.Interfaces;using AutoMapper;using AutoMapper.QueryableExtensions;using MediatR;using Microsoft.EntityFrameworkCore;namespace Application.ShortUrls.Queries.GetShortUrlList;public class GetShortUrlListQueryHandler : IRequestHandler<GetShortUrlListQuery, ShortUrlListDto>{    private readonly IShortUrlDbContext _dbContext;    private readonly IMapper _mapper;    public GetShortUrlListQueryHandler(IShortUrlDbContext dbContext, IMapper mapper) =>        (_dbContext, _mapper) = (dbContext, mapper);    public async Task<ShortUrlListDto> Handle(GetShortUrlListQuery request, CancellationToken cancellationToken)    {        var shortUrlQuery = await _dbContext.ShortUrls            .ProjectTo<ShortUrlLookupDto>(_mapper.ConfigurationProvider)            .ToListAsync(cancellationToken);        return new ShortUrlListDto() { ShortUrls = shortUrlQuery };    }}