using Application.Common.Mappings;using Application.ShortUrls.Queries.GetShortUrl;using AutoMapper;using Domain;namespace Application.ShortUrls.Queries.GetShortUrlList;public class ShortUrlLookupDto : IMapWith<ShortUrl>{    public string Destination { get; set; }    public string Path { get; set; }    public void Mapping(Profile profile)    {        profile.CreateMap<ShortUrl, ShortUrlLookupDto>()            .ForMember(shortDto => shortDto.Destination,                opt                    => opt.MapFrom(shortUrl => shortUrl.Destination))            .ForMember(shortDto => shortDto.Path,                opt                    => opt.MapFrom(shortUrl => shortUrl.Path));    }}