using Application.Interfaces;using Microsoft.Extensions.Configuration;using Microsoft.Extensions.DependencyInjection;using Microsoft.EntityFrameworkCore;namespace Infrastructure;public static class DependencyInjection{    public static IServiceCollection AddPersistence(this IServiceCollection service, IConfiguration configuration)    {        var connectionString = configuration["DBConnection"];        service.AddDbContext<ShortUrlDbContext>(options =>        {            options.UseSqlite(connectionString);        });        service.AddScoped<IShortUrlDbContext>(provider =>            provider.GetService<ShortUrlDbContext>());        return service;    }}