using Application.Interfaces;using Domain;using Infrastructure.EntityTypeConfigurations;using Microsoft.EntityFrameworkCore;namespace Infrastructure;public class ShortUrlDbContext : DbContext, IShortUrlDbContext{    public DbSet<ShortUrl> ShortUrls { get; set; }        public ShortUrlDbContext(DbContextOptions<ShortUrlDbContext> options)        : base(options){}        protected override void OnModelCreating(ModelBuilder builder)    {        builder.ApplyConfiguration(new ShortUrlConfiguration());        base.OnModelCreating(builder);    }}