using Infrastructure;using Microsoft.EntityFrameworkCore;namespace ShortUrl.Tests.Common;public class ShortUrlContextFactory{    public static string ShortUrlPathForDelete = "google";    public static string ShortUrlPathForUpdate = "amazon";    public static ShortUrlDbContext Create()    {        var options = new DbContextOptionsBuilder<ShortUrlDbContext>()            .UseInMemoryDatabase(Guid.NewGuid().ToString())            .Options;        var context = new ShortUrlDbContext(options);        context.Database.EnsureCreated();        context.ShortUrls.AddRange(            new Domain.ShortUrl            {                Destination = "https://www.google.com/search?q=youtube&oq=&aqs=chrome.0.35i39i362l8.592249133j0j7&sourceid=chrome&ie=UTF-8",                Path = "google"            },            new Domain.ShortUrl            {                Destination = "https://www.amazon.com/gp/help/customer/display.html?nodeId=508510&ref_=nav_cs_customerservice",                Path = "amazon"            },            new Domain.ShortUrl            {                Destination = "https://www.youtube.com/watch?v=9_JUDqrDdQQ",                Path = "youtube"            }        );        context.SaveChanges();        return context;    }    public static void Destroy(ShortUrlDbContext context)    {        context.Database.EnsureDeleted();        context.Dispose();    }}