using Microsoft.EntityFrameworkCore;using Application.ShortUrls.Commands.CreateShortUrl;using ShortUrl.Tests.Common;namespace ShortUrl.Tests.ShortsUrl.Commands;public class CreateShortUrlCommandHandlerTests : TestCommandBase{    [Fact]    public async Task CreateShortUrlCommandHandler_Success()    {        //Arrange        var handler = new CreateShortUrlCommandHandler(Context);        var shortUrlDestination =            "https://www.olx.com/search?q=olx&oq=&aqs=chrome.0.35i39i362l8.592249133j0j7&sourceid=chrome&ie=UTF-8";        var shortUrlPath = "olx";        //Act        await handler.Handle(            new CreateShortUrlCommand            {                Destination = shortUrlDestination,                Path = shortUrlPath            },            CancellationToken.None);        //Assert        Assert.NotNull(            await Context.ShortUrls.SingleOrDefaultAsync(shortUrl =>                shortUrl.Destination == shortUrlDestination                && shortUrl.Path == shortUrlPath            ));    }}