using Application.Common.Exceptions;using Application.ShortUrls.Commands.DeleteShortUrl;using ShortUrl.Tests.Common;namespace ShortUrl.Tests.ShortsUrl.Commands;public class DeleteShortUrlCommandHandlerTests : TestCommandBase{    [Fact]    public async Task DeleteShortUrlCommandHandler_Success()    {        //Arrange        var handler = new DeleteShortUrlCommandHandler(Context);        //Act        await handler.Handle(new DeleteShortUrlCommand        {            Path = ShortUrlContextFactory.ShortUrlPathForDelete        }, CancellationToken.None);        //Assert        Assert.Null(Context.ShortUrls.SingleOrDefault(shorUrl             => shorUrl.Path == ShortUrlContextFactory.ShortUrlPathForDelete));    }        [Fact]    public async Task DeleteShortUrlCommandHandler_FailOnWrongId()    {        //Arrange        var handler = new DeleteShortUrlCommandHandler(Context);                //Act        //Assert        await Assert.ThrowsAsync<NotFoundException>(async () =>            await handler.Handle(                new DeleteShortUrlCommand                {                    Path = "notCorrectPath",                }, CancellationToken.None));    }}