using Application.Common.Exceptions;using Application.ShortUrls.Commands.UpdateShortUrl;using Microsoft.EntityFrameworkCore;using ShortUrl.Tests.Common;namespace ShortUrl.Tests.ShortsUrl.Commands;public class UpdateShortUrlCommandHandlerTests : TestCommandBase{    [Fact]    public async Task UpdateShortUrlCommandHandler_Success()    {        //Arrange        var handler = new UpdateShortUrlCommandHandler(Context);        var updatedDestination = "new update Destination";        //Act        await handler.Handle(            new UpdateShortUrlCommand()            {                Path = ShortUrlContextFactory.ShortUrlPathForUpdate,                Destination = updatedDestination            },            CancellationToken.None);        //Assert        Assert.NotNull(            await Context.ShortUrls.SingleOrDefaultAsync(shortUrl =>                 shortUrl.Path == ShortUrlContextFactory.ShortUrlPathForUpdate                 && shortUrl.Destination == updatedDestination));    }        [Fact]    public async Task UpdateShortUrlCommandHandler_FailOnWrongId()    {        //Arrange        var handler = new UpdateShortUrlCommandHandler(Context);        //Act        //Assert        await Assert.ThrowsAsync<NotFoundException>(async () =>            await handler.Handle(                new UpdateShortUrlCommand()                {                    Path = "not correct Path"                }, CancellationToken.None));    }}