using Application.ShortUrls.Queries.GetShortUrlList;using ShortUrl.Tests.Common;using Shouldly;namespace ShortUrl.Tests.ShortsUrl.Queries;[Collection("QueryCollection")]public class GetShortUrlListQueryHandlerTests : QueryTestFixture{    [Fact]    public async Task GetShortUrlListQueryHandler_Success()    {        //Arrange        var handler = new GetShortUrlListQueryHandler(Context, Mapper);        //Act        var result = await handler.Handle(            new GetShortUrlListQuery(), CancellationToken.None        );        //Assert        result.ShouldBeOfType<ShortUrlListDto>();        result.ShortUrls.Count.ShouldBe(3);    }}