using Application.ShortUrls.Queries.GetShortUrl;using ShortUrl.Tests.Common;using Shouldly;namespace ShortUrl.Tests.ShortsUrl.Queries;[Collection("QueryCollection")]public class GetShortUrlQueryHandlerTests : QueryTestFixture{    [Fact]    public async Task GetShortUrlQueryHandler_Success()    {        //Arrange        var handler = new GetShortUrlQueryHandler(Context, Mapper);        //Act        var result = await handler.Handle(            new GetShortUrlQuery            {                Path = "google"            }, CancellationToken.None        );        //Assert        result.ShouldBeOfType<ShortUrlDto>();        result.Destination.ShouldBe("https://www.google.com/search?q=youtube&oq=&aqs=chrome.0.35i39i362l8.592249133j0j7&sourceid=chrome&ie=UTF-8");    }}