using Application.Common.Mappings;using Application.ShortUrls.Commands.CreateShortUrl;using AutoMapper;namespace WebApi.Models;public class CreateShortUrlDto : IMapWith<CreateShortUrlCommand>{    public string Destination { get; set; }    public string Path { get; set; }        public void Mapping(Profile profile)    {        profile.CreateMap<CreateShortUrlDto, CreateShortUrlCommand>()            .ForMember(createCommand => createCommand.Destination,                opt => opt.MapFrom(shortDto => shortDto.Destination))            .ForMember(createCommand => createCommand.Path,                opt => opt.MapFrom(shortDto => shortDto.Path));    }}