using Application.Common.Mappings;using Application.ShortUrls.Commands.UpdateShortUrl;using AutoMapper;namespace WebApi.Models;public class UpdateShortUrlDto: IMapWith<UpdateShortUrlCommand>{    public string Destination { get; set; }    public string Path { get; set; }        public void Mapping(Profile profile)    {        profile.CreateMap<UpdateShortUrlDto, UpdateShortUrlCommand>()            .ForMember(updateCommand => updateCommand.Destination,                opt => opt.MapFrom(shortDto => shortDto.Destination))            .ForMember(updateCommand => updateCommand.Path,                opt => opt.MapFrom(shortDto => shortDto.Path));    }}